(1, 'essential', 'en', 'Essential', 'Essential cookies enable basic functions and are necessary for the proper function of the website.', 1, 1, 1, 1),
(2, 'statistics', 'en', 'Statistics', 'Statistics cookies collect information anonymously. This information helps us to understand how our visitors use our website.', 1, 2, 1, 1),
(3, 'marketing', 'en', 'Marketing', 'Marketing cookies are used by third-party advertisers or publishers to display personalized ads. They do this by tracking visitors across websites.', 1, 3, 1, 1),
(4, 'external-media', 'en', 'External Media', 'Content from video platforms and social media platforms is blocked by default. If External Media cookies are accepted, access to those contents no longer requires manual consent.', 1, 4, 1, 1),
